import React, { useEffect, useState } from 'react'
import { AppBar, Avatar, Button, Toolbar, Typography } from '@material-ui/core';
import useStyles from './styles'
import { useDispatch } from 'react-redux';
import memories from '../../images/memories.png'
import { Link, useHistory, useLocation } from 'react-router-dom';
import decode from 'jwt-decode';


export const Navbar = () => {
    const classes = useStyles();
    const [user, setUser] = useState(JSON.parse(localStorage.getItem('profile')));
    const dispatch = useDispatch();
    const history = useHistory();
    const location = useLocation();

    const logout = () => {
        dispatch({ type: 'LOGOUT' });
        history.push('/');
        setUser(null);
    };

    useEffect(() => {
        const token = user?.token;

        if (token) {
            const decodedToken = decode(token);

            if (decodedToken.exp * 1000 < new Date().getTime()) logout();
          } else {
              return
          }

        setUser(JSON.parse(localStorage.getItem('profile')))
    }, [location, user.token]);

    return (
    <AppBar className={classes.appBar} position="static" color="inherit">
    <div className={classes.brandContainer}>
    <Typography component={Link} to='/' className={classes.heading} variant="h2" align="center">Memories</Typography>
    <img className={classes.image} src={memories} alt="icon" height="60"></img>
    </div>
    <Toolbar className={classes.toolbar}>
        {user ? (
            <div className={classes.profile}>
                <Avatar className={classes.purple} alt={user.result.name} src={user.result.imageURL}>{user.result.name.charAt(0)}</Avatar>
                <Typography className={classes.userName} varient='h6'>{user.result.name}</Typography>
                <Button varient='contained' classname={classes.logout} color='secondary' onClick={logout}>Logout</Button>
            </div>
        ) : (
            <Button component={Link} to='/auth' variant='contained' color='primary'>Sign In</Button>
        )}
    </Toolbar>
    </AppBar>
  );
};

export default Navbar;
